
public class Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	       int i,k;
	       i= 2;
	       System.out.println("Table of "+i); 
	       for(int j=1; j<=10; j++)
	       {
	           k=i*j;
	           System.out.println(i+" * "+j+" = "+k);
	}

}}
