
public class Sumguvi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0,odd=0;
for(int i=1;i<=45;i++){
	if(i<=15){
		sum=sum+i;
		
	}if(i>=15){
		if(i%2!=0){
			 odd=odd+i;
			
		}
	}}System.out.println(sum);System.out.println(odd);
}
	}

